import React from "react"
import User from "./user"


class Users extends React.Component {
 constructor(props) {
    super(props)
    this.state = {
        users: [
            {
                id: 1,
                firstname: 'Bob',
                lastname: 'Marley',
                bio: 'lorem ipsum dolor sit amet consecteur',
                age: 40,
                isHappy: true
            },
            {
                id: 2,
                firstname: 'John',
                lastname: 'Doe',
                bio: 'lorem ipsum dolor sit amet consecteur',
                age: 22,
                isHappy: false
            }
         ]
    }
 }
 
 render() {
    if (this.state.users.length > 0)
        return(<div>
            {this.state.users.map((el) => (<div className="user" key={el.id}>
                <h3>{el.firstname} {el.lastname}</h3>
                <p>{el.bio}</p>
                <b>{el.isHappy ? 'Scastliv :)' : 'Ne osobo :('}</b>
            </div>))}
        </div>)
    else 
        return (<div className="user">
            <h3>Polzovateley net</h3>
        </div>)
  }
}

export default Users
